import React, { useState } from 'react';
import axios from 'axios';

const PopupFormButton = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    creator: '',
    date: ''
  });

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Make a POST request to your Flask backend
      const response = await axios.post('/api/slide-decks/create', formData);
      console.log('Form submitted successfully:', response.data);
      // Close the popup after form submission
      setShowPopup(false);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div>
      <button onClick={togglePopup}>Show Popup Form</button>
      {showPopup && (
        <div className="popup">
          <div className="popup-inner">
            <h2>Popup Form</h2>
            <form onSubmit={handleSubmit}>
              {/* Form fields */}
              <label htmlFor="name">Name:</label>
              <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
              <label htmlFor="creator">Creator:</label>
              <input type="text" id="creator" name="creator" value={formData.creator} onChange={handleChange} />
              <label htmlFor="date">Date:</label>
              <input type="text" id="date" name="date" value={formData.date} onChange={handleChange} />
              <button type="submit">Submit</button>
            </form>
            <button onClick={togglePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PopupFormButton;
