import React from "react";

const FileDropdown = () => {
    const handleOptionClick = (option) => {
      // Handle the click event for each option
      console.log(`Clicked ${option} option`);
      // Add your logic for each option here (e.g., open a modal, save the file, etc.)
    };

    return (
      <div className="file-dropdown">
        <button className="file-dropdown-button">File</button>
        <div className="file-dropdown-content">
          <div onClick={() => handleOptionClick('New')}>New</div>
          <div onClick={() => handleOptionClick('Save')}>Save</div>
          <div onClick={() => handleOptionClick('Save As')}>Save As</div>
          <div onClick={() => handleOptionClick('Save All')}>Save All</div>
          <div onClick={() => handleOptionClick('Save and Export Deck As')}>Save and Export Deck As</div>
        </div>
      </div>
    );
};

export default FileDropdown;
