import React, { useState } from 'react';

const PopupFormButton = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    creator: '',
    date: new Date().toISOString().slice(0, 10) // Autofill with current date
  });

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('/api/slide-decks/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        console.log('Form data sent successfully');
        setFormData({
          name: '',
          creator: '',
          date: new Date().toISOString().slice(0, 10)
        })
        setShowPopup(false);
      } else {
        console.error('Failed to send form data');
      }
    } catch (error) {
      console.error('Error sending form data:', error);
    }
  };

  return (
    <div>
      <button onClick={togglePopup}>Show Popup Form</button>
      {showPopup && (
        <div className="popup">
          <div className="popup-inner">
            <h2>Popup Form</h2>
            <form onSubmit={handleSubmit}>
              <label htmlFor="name">Name:</label>
              <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
              <label htmlFor="creator">Creator:</label>
              <input type="text" id="creator" name="creator" value={formData.creator} onChange={handleChange} />
              <label htmlFor="date">Date:</label>
              <input type="date" id="date" name="date" value={formData.date} onChange={handleChange} />
              <button type="submit">Submit</button>
            </form>
            <button onClick={togglePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PopupFormButton;
