import React, { useState } from 'react';

const MenuBar = () => {
  const [fileDropdownOpen, setFileDropdownOpen] = useState(false);
  const [homeDropdownOpen, setHomeDropdownOpen] = useState(false);

  const toggleFileDropdown = () => {
    setFileDropdownOpen(!fileDropdownOpen);
  };

  const toggleHomeDropdown = () => {
    setHomeDropdownOpen(!homeDropdownOpen);
  };

  const handleFileOptionClick = (option) => {
    // Handle file option click logic here
    console.log(`File Option Clicked: ${option}`);
  };

  const handleHomeOptionClick = (option) => {
    // Handle home option click logic here
    console.log(`Home Option Clicked: ${option}`);
  };

  return (
    <div className="menu-bar">
      <div className="menu-item" onClick={toggleFileDropdown}>
        File
        {fileDropdownOpen && (
          <div className="dropdown">
            <div className="option" onClick={() => handleFileOptionClick('New')}>
              New
            </div>
            <div className="option" onClick={() => handleFileOptionClick('Open')}>
              Open
            </div>
            <div className="option" onClick={() => handleFileOptionClick('Save')}>
              Save
            </div>
          </div>
        )}
      </div>

      <div className="menu-item" onClick={toggleHomeDropdown}>
        Home
        {homeDropdownOpen && (
          <div className="dropdown">
            <div className="option" onClick={() => handleHomeOptionClick('Cut')}>
              Cut
            </div>
            <div className="option" onClick={() => handleHomeOptionClick('Copy')}>
              Copy
            </div>
            <div className="option" onClick={() => handleHomeOptionClick('Paste')}>
              Paste
            </div>
          </div>
        )}
      </div>
      {/* Add more menu items as needed */}
    </div>
  );
};

export default MenuBar;
