import React, { useEffect, useState } from "react";
import { List, ListItemButton, ListItemText, ListItemIcon } from '@mui/material';
import ListSubheader from '@mui/material/ListSubheader';
import FolderIcon from '@mui/icons-material/Folder';

function SlideList() {

    const [slideDecks, setSlideDecks] = useState([]);

    useEffect (() => {
        async function fetchSlideDecks() {
            try {
                const response = await fetch('/api/slide-decks');
                if (!response.ok) {
                    throw new Error('Failed to fetch slide decks');
                }
                const data = await response.json();
                setSlideDecks(data.slideDecks);
            } catch (error) {
                console.error('Error fetching slide decks:', error);
            }
        }

        fetchSlideDecks();
    }, []);

    return (
        <List
          subheader={
            <ListSubheader component="div" id="nested-list-subheader" color="#000000" style={{ paddingBottom: 0, paddingTop: '10px' }}>
              Name 
            </ListSubheader>
          }
        >
            {slideDecks.map((deck, index) => (
                <ListItemButton key={index} dense divider>
                    <ListItemIcon style={{padding: '5px'}}>
                        <FolderIcon style={{ height: '18px' }}/>
                    </ListItemIcon>
                    <ListItemText primary={deck} style={{ padding: '5px', textAlign: 'left'}}/>
                </ListItemButton>
            ))}
        </List>
    );
};

export default SlideList;
